        </NavLink>
      </div>
      <div id="vocabTable">
      <table className="table">
        <thead>
          <tr>
            <th scope="col"></th>
            <th scope="col">German</th>
            <th scope="col">English</th>
          </tr>
        </thead>
        <tbody>
          {vocabulary.map(item => (<tr key={item.id}>
          <th scope="row">{item.id}</th>
          <td>{item.German}</td>
          <td>{item.English}</td></tr>))}
      </tbody>
</table>
</div>
</>
