import React from 'react';
import '../App.css';

export default function Quizz(){
    return(
        <>
            <Flashcard/>
        </>
    );
}


function Flashcard() {
    const [vocabID, setVocabID] = React.useState(1);
    let vocabGerman = '';
    let vocabEnglish= '';

    async function handleClick() {
        let res = await fetch(`/api/quizz?data=${vocabID.toString()}`);
        res = await res.json();
        vocabGerman = res.german;
        vocabEnglish = res.english;
        setVocabID(vocabID+1);
        console.log(vocabGerman);
    }

    return(
        <>
        <div className="flip-card">
            <div className="flip-card-inner">
                <div className="flip-card-front">{vocabGerman}Example</div>
                <div className="flip-card-back">{vocabEnglish}Beispiel</div>
            </div>
        </div>
        <button className="btn btn-primary" type="button" onClick={handleClick}>Next </button>
    </>
    );
}
