import React from 'react';

import eldercare from '../Vocabulary/ElderCare.json';
import construction from '../Vocabulary/Construction.json';
import { NavLink } from 'react-router-dom';
import '../App.css';

export default function Vocabulary(props) {
  const domain = props.domain;

  if (domain === "eldercare"){
    return (
      <>
        <VocabTable Vocab={eldercare.Vocabulary} domain={domain}/>
      </>
    )
  } else {
    return (
      <>
        <VocabTable Vocab={construction.Vocabulary} domain={domain}/>
      </>
    )
  }
}


  function VocabTable(props){
    return(
      <>
      <div id="learn">
        <button type="button" className="btn btn-primary">
          <NavLink to={`/${props.domain}/quizz`} className="learn-link">Learn</NavLink>
        </button>
      </div>
      <div id="vocabTable">
      <table className="table">
        <thead>
          <tr>
            <th scope="col"></th>
            <th scope="col">German</th>
            <th scope="col">English</th>
          </tr>
        </thead>
        <tbody>
          {props.Vocab.map(item => (<tr key={item.id}>
          <th scope="row">{item.id}</th>
          <td>{item.German}</td>
          <td>{item.English}</td>
          <td><Example key = {item.id} vocabID={item.id} domain={props.domain}/></td></tr>))}
      </tbody>
</table>
</div>
</>
  )
}

function Example(props) {
  const [example, setExample] = React.useState('');
  const id = props.vocabID;

  async function handleClick() {
      let res = await fetch(`/api/${props.domain}?data=${id}`);
      res = await res.json();
      setExample(res.example);
  }


  return(
    <form id="example-form">
      <button type="button" className="btn btn-primary" onClick={handleClick}>Generate</button> 
      <div id="example">{example}</div>
    </form>
  )
}
