import React from 'react';
import '../App.css';

export default function Quizz(props){
    return(
        <>
            <Flashcard domain={props.domain}/>
        </>
    );
}


function Flashcard(props) {
    const [vocabID, setVocabID] = React.useState(1);
    const [vocabGerman, setVocabGerman] = React.useState('');
    const [vocabEnglish, setVocabEnglish] = React.useState('');

    async function handleClick() {
        let res = await fetch(`/api/quizz?data=${vocabID.toString()}&${props.domain}`);
        res = await res.json();
        setVocabID(vocabID+1);
        setVocabGerman(res.german);
        setVocabEnglish(res.english);
    }

    return(
        <>
        <div className="flip-card-container">
        <div className="flip-card">
            <div className="flip-card-inner">
                <div className="flip-card-front">{vocabEnglish}</div>
                <div className="flip-card-back">{vocabGerman}</div>
            </div>
        </div>
        <button id= "next-button" className="btn btn-outline-dark" type="button" onClick={handleClick}>Next </button>
        <button id= "prev-button" className="btn btn-outline-dark" type="button" onClick={handleClick}>Prev</button>
        <div id="generate-example"><Example vocabID={vocabID}/></div>
        </div>

    </>
    );
}

function Example(props) {
    const [example, setExample] = React.useState('');
    const id = props.vocabID;
    const domain = props.domain;

    async function handleClick() {
        let res = await fetch(`/api/example?data=${id}&domain=${domain}`);
        res = await res.json();
        setExample(res.example);
    }


    return(
      <form id="example-sentence-form">
        <button type="button" className="btn btn-outline-dark" onClick={handleClick}>Generate</button> 
        <div id="example-sentence">{example}</div>
      </form>
    )
  }
