import React from 'react';
import { useLocation } from 'react-router-dom';
import '../App.css';

export default function Quizz(){
    return(
        <>
            <Flashcard/>
        </>
    );
}


function Flashcard() {
    const [vocabID, setVocabID] = React.useState(1);
    const [vocabGerman, setVocabGerman] = React.useState('');
    const [vocabEnglish, setVocabEnglish] = React.useState('');
    const pathname = useLocation().pathname;

    async function handleClick() {
        let res = await fetch(`/api${pathname}?data=${vocabID.toString()}`);
        res = await res.json();
        setVocabID(vocabID+1);
        setVocabGerman(res.german);
        setVocabEnglish(res.english);
    }

    return(
        <>
        <div className="flip-card-container">
        <div className="flip-card">
            <div className="flip-card-inner">
                <div className="flip-card-front">{vocabEnglish}</div>
                <div className="flip-card-back">{vocabGerman}</div>
            </div>
        </div>
        <button id= "next-button" className="btn btn-outline-dark" type="button" onClick={handleClick}>Next </button>
        </div>
    </>
    );
}
