import React from 'react';

import eldercare from '../Vocabulary/ElderCare.json';
import construction from '../Vocabulary/Construction.json';
import { NavLink } from 'react-router-dom';
import '../App.css';

export default function Vocabulary(props) {
  const domain = props.domain;

  if (domain === "eldercare"){
    return (
      <>
        <VocabTable domain={domain}/>
      </>
    )
  } else {
    return (
      <>
        <VocabTable domain={domain}/>
      </>
    )
  }
}


  async function VocabTable(props){
    const vocab = await fetch(`/api/table?domain=${domain}`);

    return(
      <>
      <div id="learn">
        <NavLink to={`/${props.domain}/quizz`} className="learn-link">
          <button type="button" className="btn btn-outline-dark">Learn</button>
        </NavLink>
      </div>
      <div id="vocabTable">
      <table className="table">
        <thead>
          <tr>
            <th scope="col"></th>
            <th scope="col">German</th>
            <th scope="col">English</th>
          </tr>
        </thead>
        <tbody>
          {vocab.map(item => (<tr key={item.id}>
          <th scope="row">{item.id}</th>
          <td>{item.German}</td>
          <td>{item.English}</td></tr>))}
      </tbody>
</table>
</div>
</>
  )
}
