import React from 'react'

import ConstructionData from '../Vocabulary/Construction.json';
import '../App.css';

export default function Construction() {
  return (
    <>
      <Vocabulary Vocab={ConstructionData.Craftsman_Vocabulary} />
    </>
  )
}


function Vocabulary({Vocab}){
  return(
    <div id="vocabTable">
    <table class="table">
      <thead>
        <tr>
          <th scope="col"></th>
          <th scope="col">German</th>
          <th scope="col">English</th>
          <th scope="col">Example</th>
        </tr>
      </thead>
      <tbody>
        {Vocab.map(item => (<tr key={item.id}>
        <th scope="row">{item.id}</th>
        <td>{item.German}</td>
        <td>{item.English}</td>
        <td><Example input={item.id}/></td></tr>))}
      </tbody>
</table>
</div>
  )
}

function Example({id}) {
  const [data, setData] = React.useState(null);

  const handleClick = async () => {
    React.useEffect(() => {
      fetch("/api/getRequest?data=id")
        .then((res) => res.json())
        .then((data) => setData(data.message));
    }, []);
  }

  return(
    <form>
      <div id="whitespace">{data}</div>
      <button onClick={handleClick}>Generate</button>
    </form>
  )
}
