import React from 'react';

import ElderCareData from '../Vocabulary/ElderCare.json';
import '../App.css';
import { NavLink } from 'react-router-dom';

export default function ElderCare() {
  return (
    <>
      <Vocabulary Vocab={ElderCareData.Vocabulary} />
    </>
  )
}


  function Vocabulary({Vocab}){
    return(
      <>
      <button type="button" className="btn btn-primary"><div><NavLink to="/eldercare/quizz" className="learn-link">Learn</NavLink></div></button>
      <div id="vocabTable">
      <table className="table">
        <thead>
          <tr>
            <th scope="col"></th>
            <th scope="col">German</th>
            <th scope="col">English</th>
          </tr>
        </thead>
        <tbody>
          {Vocab.map(item => (<tr key={item.id}>
          <th scope="row">{item.id}</th>
          <td>{item.German}</td>
          <td>{item.English}</td>
          <td><Example key = {item.id} vocabID={item.id}/></td></tr>))}
      </tbody>
</table>
</div>
</>
  )
}

function Example(props) {
  const [example, setExample] = React.useState('');
  let id = props.vocabID;

  async function handleClick() {
      let res = await fetch(`/api/example?data=${id}`);
      res = await res.json();
      setExample(res.example);
  }


  return(
    <form id="example-form">
      <button type="button" className="btn btn-primary" onClick={handleClick}>Generate</button> 
      <div id="example">{example}</div>
    </form>
  )
}
