import React from 'react';
import '../App.css';

export default function Quizz(){
    return(
        <>
            <Flashcard/>
        </>
    );
}


function Flashcard() {
    const [vocabID, setVocabID] = React.useState(0);
    let vocabGerman = '';
    let vocabEnglish= '';

    async function handleClick() {
        let res = await fetch(`/api/quizz?data=${vocabID.toString()}`);
        res = await res.json();
        vocabGerman = res.vocabGerman;
        vocabEnglish = res.vocabEnglish;
        setVocabID(vocabID+1);
    }

    return(
    <>
        <div class="flip-card">
            <div class="flip-card-inner">
                <div class="flip-card-front">{vocabGerman}</div>
                <div class="flip-card-back">{vocabEnglish}</div>
            </div>
        </div>
        <button class="btn btn-primary" type="button" onClick={handleClick}>Next </button>
    </>
    );
}
