import React from 'react';
import '../App.css';

export default function Quizz(){
    return(
        <>
            <Flashcard/>
        </>
    );
}


function Flashcard() {
    const [vocabID, setVocabID] = React.useState(1);
    const [vocabGerman, setVocabGerman] = React.useState('');
    const [vocabEnglish, setVocabEnglish] = React.useState('');

    async function handleClick() {
        let res = await fetch(`/api/quizz?data=${vocabID.toString()}`);
        res = await res.json();
        setVocabID(vocabID+1);
        setVocabGerman(res.german);
        setVocabEnglish(res.english);
    }

    return(
        <>
        <div className="flip-card">
            <div className="flip-card-inner">
                <div className="flip-card-front"><span>{vocabGerman}E</span></div>
                <div className="flip-card-back"><span id="vocab">{vocabEnglish}</span></div>
            </div>
        </div>
        <button className="btn btn-primary" type="button" onClick={handleClick}>Next </button>
    </>
    );
}
