import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  domains: ['Eldercare', 'Construction'], // Initially, no user is logged in
};

const authSlice = createSlice({
  name: 'domain',
  initialState,
  reducers: {
    domainSuccess: (state, action) => {
      state.domains = action.payload;
    },
    registerDomainSuccess: (state, action) => {
        state.domains = action.payload;
    },
    getDomainSuccess: (state, action) => {
        state.domains = action.payload;
    }
  }
});

export const { domainSuccess, registerDomainSuccess, getDomainSuccess } = authSlice.actions;
export default authSlice.reducer;
