import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  domain: null, // Initially, no user is logged in
};

const authSlice = createSlice({
  name: 'domain',
  initialState,
  reducers: {
    domainSuccess: (state, action) => {
      state.domain = action.payload;
    },
    registerDomainSuccess: (state, action) => {
        state.domain = action.payload;
    }
  }
});

export const { domainSuccess, registerDomainSuccess } = authSlice.actions;
export default authSlice.reducer;
