import React from 'react';
import { NavLink } from 'react-router-dom';
import '../App.css';

export default function Vocabulary(props) {
  return (
    <>
      <VocabTable domain={props.domain}/>
    </>
  )
}


  const VocabTable = async (props) => {
    let vocab = await fetch(`/api/table?domain=${props.domain}`);
    vocab = vocab.json();

    return(
      <>
      <div id="learn">
        <NavLink to={`/${props.domain}/quizz`} className="learn-link">
          <button type="button" className="btn btn-outline-dark">Learn</button>
        </NavLink>
      </div>
      <div id="vocabTable">
      <table className="table">
        <thead>
          <tr>
            <th scope="col"></th>
            <th scope="col">German</th>
            <th scope="col">English</th>
          </tr>
        </thead>
        <tbody>
          {vocab.map(item => (<tr key={item.id}>
          <th scope="row">{item.id}</th>
          <td>{item.German}</td>
          <td>{item.English}</td></tr>))}
      </tbody>
</table>
</div>
</>
  )
}
